import { useState } from "react";
import { Link } from "react-router-dom";
import { RESPONSE } from "../App";
import "./styles.css";
import { ERROR, PRODUCERDATA } from "../types";

export const Manufacturer = () => {
  const [response, setResponse] = useState<RESPONSE | undefined | PRODUCERDATA>(undefined);
  const [error, setError] = useState<ERROR | undefined>()
  const [id, setId] = useState<string | null>(null);
  const handleData = async () => {
    const _tx = await fetch("http://localhost:5000/api/getProduct/" + id, {
      method: 'GET',

      headers: {
        'Content-Type': 'application/pdf',
      },
    });
    let _response = await _tx.json();
    if(_response.status){
      _response = JSON.parse(_response.status)
    }else{
      setResponse(_response);
    }
  };

  return (
    <div>
      <h1>Manufacturer</h1>
      {response?.error ? (
        <div className="form">
          <h3 style={{color:"red"}}>{response.error}</h3>
          <button onClick={() => {setResponse(undefined); setId('')}}>Try Again</button>
        </div>
      ) : response ? (
        <ul>
          {Object.keys(response).map((val) => <li>{response[val]}</li>)}
        </ul>
      ) : (
        <div className="form">
          <input
            name="id"
            type="text"
            className="feedback-input"
            placeholder="Enter Product ID"
            onChange={(e) => setId(e.target.value)}
          />
          <button onClick={handleData}>GET</button>
        </div>
      )}

      <Link to="/">Home</Link>
    </div>
  );
};
