import { lazy, useState } from "react";
import { Link } from "react-router-dom";
import { RESPONSE } from "../App";
import download from 'downloadjs';
import "./styles.css";
import { readFile } from "fs";

export const Manufacturer = () => {
  const [response, setResponse] = useState<RESPONSE | undefined>(undefined);
  const [id, setId] = useState<string | null>(null);
  const [pdfFile, setPdfFile] = useState("");
  const handleData = async () => {
    const _tx = await fetch("http://localhost:5000/api/getTuna/" + id, {
      method: 'GET',

      headers: {
        'Content-Type': 'application/pdf',
      },
    });
    const _response = await _tx.blob();
    const url = window.URL.createObjectURL(new Blob([_response]));

    const _pdf = readFile(url, (e) => {
      console.log(e)
    })
    console.log(url);
    download(url)
    // setResponse({status:url});
  };

  return (
    <div>
      <h1>Manufacturer</h1>
      {response?.error ? (
        <>
          <h3>{response.error}</h3>
          <button>Try Again</button>
        </>
      ) : response?.status ? (
        <a href={response.status} target="_blank">
          Download PDF
        </a>
      ) : (
        <div className="form">
          <input
            name="id"
            type="text"
            className="feedback-input"
            placeholder="Enter Product ID"
            onChange={(e) => setId(e.target.value)}
          />
          <button onClick={handleData}>GET</button>
        </div>
      )}

      <Link to="/">Home</Link>
    </div>
  );
};
