import { useState } from "react";
import { Link } from "react-router-dom";
import { RESPONSE } from "../types";
import "./styles.css";

export const GetHistory = () => {
  const [response, setResponse] = useState<RESPONSE | undefined>(undefined);
  const [id, setId] = useState<string | null>(null);

  const handleData = async () => {
    const _tx = await fetch("http://localhost:5000/api/getHistory/" + id, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    });
    const _response = await _tx.json();
    console.log(JSON.parse(_response))
    setResponse(_response);
  };
  return (
    <div>
      <h1>Get Product History</h1>
      {response?.error ? (
        <div className="form">
          <h3 style={{color:"red"}}>{response.error}</h3>
          <button onClick={() => {setResponse(undefined); setId('')}}>Try Again</button>
        </div>
      ) : response?.status ? (
        <a href={response.status} target="_blank">
          Download PDF
        </a>
      ) : (
        <div className="form">
          <input
            name="id"
            type="text"
            className="feedback-input"
            placeholder="Enter Product ID"
            onChange={(e) => setId(e.target.value)}
          />
          <button onClick={handleData}>GET</button>
        </div>
      )}
      <Link to="/">Home</Link>
    </div>
  );
};
