import { useState } from "react";
import { Link } from "react-router-dom";
import { RESPONSE } from "../App";
import { ERROR, PRODUCERDATA } from "../types";
import "./styles.css";

export const Manufacturer = () => {
  const [response, setResponse] = useState<PRODUCERDATA | null>(null);
  const [error, setError] = useState<ERROR | undefined>(undefined)
  const [id, setId] = useState<string | null>(null);
  const handleData = async () => {
    const _tx = await fetch("http://localhost:5000/api/getProduct/" + id, {
      method: 'GET',

      headers: {
        'Content-Type': 'application/pdf',
      },
    });
    let _response = await _tx.json();
    if(_response.status){
      _response = JSON.parse(_response.status);
      setResponse(_response)
    }else{
      setError(_response);
    }
  };

  return (
    <div>
      <h1>Manufacturer</h1>
      {error ? (
        <div className="form">
          <h3 style={{color:"red"}}>{error.error}</h3>
          <button onClick={() => {setError(undefined); setId('')}}>Try Again</button>
        </div>
      ) :response ? (
        <div className="form">
          <h2>Product with ID: ${id}</h2>
        <ul>
          {Object.keys(response).map((val) => <li> <span>{val}: </span>{(response as any)[val]}</li>)}
        </ul>
        </div>
      ) : (
        <div className="form">
          <input
            name="id"
            type="text"
            className="feedback-input"
            placeholder="Enter Product ID"
            onChange={(e) => setId(e.target.value)}
          />
          <button onClick={handleData}>GET</button>
        </div>
      )}

      <Link to="/">Home</Link>
    </div>
  );
};
