import { useState } from "react";
import { Link } from "react-router-dom";
import { RESPONSE } from "../App";
import "./styles.css";



export const Producer = () => {


  type PRODUCERDATA = {
    id:string
    longitude:string
    latitude:string
    length:string
    weight:string
  };

  const [producerData, setProducerData] = useState<PRODUCERDATA | null>(null);

  const [response, setResponse] = useState<RESPONSE | undefined>(undefined);

  const handleData = async () => {
    console.log(producerData)
    const _tx = await fetch('http://localhost:5000/api/addTuna',{
      method:'POST',
      body:JSON.stringify(producerData),
      headers: {
        'Content-Type': 'application/json',
        'Connection': 'keep-alive',
        'Accept': '*/*',
    },
    });
    setResponse(await _tx.json());
  };



  return (
    <div>
      <h1>Producer</h1>
      {response ?
       <div className="response">
        <h2><span>Status:</span> {response.status}</h2>
        <h3><span>TxID:</span> {response.txid}</h3>
        <button onClick={() => {
          setProducerData(null);
          setResponse(undefined)
        }}>Add Tuna</button>
        </div>
        :
      <div className="form">
        <input
          name="id"
          type="text"
          className="feedback-input"
          placeholder="Product ID"
          onChange={(e) => setProducerData((val:any) =>  ({...val, id:e.target.value}))}
        />
        <input
          name="longitude"
          type="text"
          className="feedback-input"
          placeholder="Longitude"
          onChange={(e) => setProducerData((val:any) =>  ({...val, longitude:e.target.value}))}

        />
        <input
          name="latitude"
          type="text"
          className="feedback-input"
          placeholder="Latitiude"
          onChange={(e) => setProducerData((val:any) =>  ({...val, latitude:e.target.value}))}

        />
        <input
          name="length"
          type="text"
          className="feedback-input"
          placeholder="Length"
          onChange={(e) => setProducerData((val:any) =>  ({...val, length:e.target.value}))}
        />
        <input
          name="weight"
          type="text"
          className="feedback-input"
          placeholder="Weight"
          onChange={(e) => setProducerData((val:any) =>  ({...val, weight:e.target.value}))}
        />
        <button onClick={handleData}>Add</button>
      </div>
      }
        <Link to='/'>Home</Link>
    </div>
  );
};
