import { useState } from "react";
import "./styles.css";

export const Producer = () => {

  const [producerData, setProducerData] = useState({
    id:"",
    longitude:"",
    latitude:"",
    length:"",
    weight:""
  });

  const handleData = async () => {
    console.log(producerData)
    const _tx = await fetch('http://localhost:5000/api/addTuna',{
      method:'POST',
      body:JSON.stringify(producerData),
      headers: {
        'Content-Type': 'application/json',
        'Connection': 'keep-alive',
        'Accept': '*/*',
    },
    });
    console.log(await _tx.json())
  }



  return (
    <div>
      <h1>Producer</h1>
      <div className="form">
        <input
          name="id"
          type="text"
          className="feedback-input"
          placeholder="Product ID"
          onChange={(e) => setProducerData({...producerData, id:e.target.value})}
        />
        <input
          name="longitude"
          type="text"
          className="feedback-input"
          placeholder="Longitude"
          onChange={(e) => setProducerData({...producerData, longitude:e.target.value})}

        />
        <input
          name="latitude"
          type="text"
          className="feedback-input"
          placeholder="Latitiude"
          onChange={(e) => setProducerData({...producerData, latitude:e.target.value})}

        />
        <input
          name="length"
          type="text"
          className="feedback-input"
          placeholder="Length"
          onChange={(e) => setProducerData({...producerData, length:e.target.value})}
        />
        <input
          name="weight"
          type="text"
          className="feedback-input"
          placeholder="Weight"
          onChange={(e) => setProducerData({...producerData, weight:e.target.value})}
        />
        <button onClick={handleData}>Add</button>
      </div>
      {/* 
<form action="http://127.0.0.1:5000/api/addTuna" method="POST">      

        </form>    */}
      <a href="/">Home</a>
    </div>
  );
};
