import { useState } from "react";
import { Link } from "react-router-dom";
import { RESPONSE } from "../App";
import "./styles.css";

export const Deliverer = () => {
  type DELIVERERDATA = {
    id: string;
    longitude: string;
    latitude: string;
  };

  const [response, setResponse] = useState<RESPONSE | undefined>(undefined);
  const [delivererData, setDelivererData] = useState<DELIVERERDATA | null>(
    null
  );

  const handleData = async () => {
    console.log(delivererData);
    const _tx = await fetch("http://localhost:5000/api/setPosition", {
      method: "POST",
      body: JSON.stringify(delivererData),
      headers: {
        "Content-Type": "application/json",
        Connection: "keep-alive",
        Accept: "*/*",
      },
    });
    setResponse(await _tx.json());

  };

  console.log(response)
  return (
    <div>
      <h1>Deliverer</h1>
      {response ? (
        <div className="response">
          <h2>
            <span>Status:</span> {response.status}
          </h2>
          <h3>
            <span>TxID:</span> {response.txid}
          </h3>
          <button
            onClick={() => {
              setDelivererData(null);
              setResponse(undefined);
            }}
          >
            Update Location
          </button>
        </div>
      ) : (
        <div className="form">
          <input
            name="id"
            type="text"
            className="feedback-input"
            placeholder="Enter Product ID"
            onChange={(e) => setDelivererData((val:any) => ({...val, id:e.target.value}))}
          />
          <input
            name="longitude"
            type="text"
            className="feedback-input"
            placeholder="Enter Longitude"
            onChange={(e) => setDelivererData((val:any) => ({...val, longitude:e.target.value}))}
          />
          <input
            name="latitude"
            type="text"
            className="feedback-input"
            placeholder="Enter Latitiude"
            onChange={(e) => setDelivererData((val:any) => ({...val, latitude:e.target.value}))}
          />
          <button onClick={handleData}>UPDATE</button>
        </div>
      )}

      <Link to="/">Home</Link>
    </div>
  );
};
